sum(
  rate(
    envoy_cluster_upstream_rq_xx{namespace="${contour_namespace}",envoy_cluster_name=~"${namespace}_${service}_\\d+", envoy_response_code_class!~"4|5" }[${__range_s}s]
      )
   )   
/ 
(
  sum(
    rate(
      envoy_cluster_upstream_rq_xx{namespace="${contour_namespace}",envoy_cluster_name=~"${namespace}_${service}_\\d+"}[${__range_s}s]
        )
     ) - 
  (
  sum(
    rate(
      envoy_cluster_upstream_rq{namespace="${contour_namespace}",envoy_cluster_name=~"${namespace}_${service}_\\d+", envoy_response_code=~"404"}[${__range_s}s]
